import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import "./home.css";

const HomePage = () => {
  const [menuItems, setMenuItems] = useState([]);
  const [error, setError] = useState("");

  const navigate = useNavigate();

  useEffect(() => {
    const fetchMenuItems = async () => {
      try {
        const response = await fetch("http://localhost:5000/menu");
        if (!response.ok) throw new Error("Failed to fetch menu items");
        const data = await response.json();
        setMenuItems(data);
      } catch (err) {
        setError(err.message);
      }
    };

    fetchMenuItems();
  }, []);

  return (
    <div className="homepage">
      <nav className="navbar">
        <div className="navbar-logo">Restaurant</div>
        <div className="navbar-links">
          <a onClick={() => navigate('/login')}>Login</a>
          <a onClick={() => navigate('/register')}>Register</a>
        </div>
      </nav>

      <section className="hero-section fade-in">
        <div className="hero-content">
          <h1>Welcome to ou</h1>
          <p>Experience culinary excellence with our exquisite menu and seamless reservation system.</p>
        </div>
      </section>

      <main className="menu-section fade-in">
        <h2>Our Menu</h2>
        {error && <p className="error">{error}</p>}
        <div className="menu-items">
          {menuItems.length > 0 ? (
            menuItems.map((item) => (
              <div key={item.id} className="menu-item">
                <img src={item.image || `https://source.unsplash.com/300x200/?food,${item.name}`} alt={item.name} />
                <div className="menu-item-content">
                  <h3>{item.name}</h3>
                  <p>{item.description}</p>
                  <p className="price">${item.price.toFixed(2)}</p>
                </div>
              </div>
            ))
          ) : (
            <p>Loading menu items...</p>
          )}
        </div>
      </main>
    </div>
  );
};

export default HomePage;
